def runcmd(cmd)
    @out = "" if not @out
    @success = true if @success == nil
    @out << "<span class=\"command\">shell$ #{cmd}</span><br/>"
    res = `#{cmd} 2>&1`
    @out << res.gsub("\n", "<br/>")
    if $?.exitstatus != 0 then
      error "Command returned #{$?.exitstatus}"
    end
    res
  end

def error(s)
    @out = "" if not @out
    logger.error "ERROR: #{s}"
    @out << "<span class=\"error\">#{s}</span><br/>"
    @success = false
  end

